export const darkTheme = {
    bg: "#121212",         // Dark background
    bgLight: "#1E1E1E",     // Lighter background shade
    primary: "#4CAF50",     // Vibrant green accent color
    text_primary: "#FFFFFF", // White primary text color
    text_secondary:  "#D0D0D0", // Light grey secondary text color
